interface IconProps {
  isActive: boolean
}

export const navigationItems = [
  {
    href: '/main',
    title: '파워업 퀴즈',
    icon: PowerUpIcon,
    segments: ['main'],
  },
  {
    href: '/repository',
    title: '노트 창고',
    icon: StudyRepositoryIcon,
    segments: ['repository', 'document'],
  },
  {
    href: '/review',
    title: '복습 체크',
    icon: ReviewCheckIcon,
    segments: ['review'],
  },
  {
    href: '/profile',
    title: '설정',
    icon: ProfileIcon,
    segments: ['profile'],
  },
]

export const findActiveNavItem = (currentSegments: string[]) => {
  const activeItem = navigationItems.find((item) => {
    return item.segments.some((segment) => currentSegments.includes(segment))
  })

  return activeItem
}

